create database shopeeinvoice;
use shopeeinvoice;

create table ITEM(
ItemNo      VARCHAR(9),
ItemDesc    VARCHAR(30) ,
PRIMARY KEY (ItemNo)
);

create table Location(
id numeric(6),
City varchar(20),
Distric varchar(40),
primary key(id)
);

create table Customer(
Cusid numeric(6),
Givenname varchar(50),
Surname varchar(50),
Address varchar(200),
id numeric(6),
Contactnumber varchar(15),
primary key(Cusid),
foreign key (id) references location(id)
);


create table  Shop(
Shopid numeric(4),
ShopName varchar(30),
LINK_SHOP varchar(50),
Contact_Number varchar(15),
Address varchar(200),
ITEMNO varchar(9),
primary key(Shopid),
foreign key (ITEMNO)references ITEM(ITEMNO)
);

create table Product(
Proid numeric(4),
ProName varchar(50),
Price decimal(10,2),
SortDescrible varchar(300),
BrandName varchar(30),
Model varchar(30),
UnitType varchar(10),
Shopid numeric(4),
primary key (Proid),
foreign key(Shopid) References Shop(Shopid)
);

create table Method(
MethodId numeric(1),
Metypes char(10),
primary key(MethodId)
);
create table Shipping(
BrandName varchar(20),
Transport_fee decimal(10,2),
Shipid numeric(1),
primary key(Shipid)
);


create table Employee(
FullName char(100),
EmId numeric(6),
ContactNumber numeric(12),
Email varchar(30),
primary key(EmId)
);

Create table Discount(
ID numeric(3),
Start_Date datetime,
End_Date datetime,
Discout_VALUESs varchar(3),
SHOPID  numeric(4),
primary key (ID),
foreign key (SHOPID)references SHOP(SHOPID)
);

create table invoicce( 
InvoiceNo    numeric(6),
Cusid     numeric(6) ,
InvDate      datetime ,
PROID numeric(4),
shopid numeric(4),
Quantity     numeric,
 PRIMARY KEY (InvoiceNo),
 foreign key (Cusid) references Customer(Cusid),
 foreign key(PROID)references PRODUCT(PROID),
 foreign key(shopid)references SHOP(shopid)
 );

create table Payment(
Payid numeric(3),
Cusid numeric(6),
EMID numeric(6),
date_order datetime,
invoiceno numeric(6),
SHIPID numeric(1),
AMOUNT_PAY decimal(10,2),
shopid numeric(4),
unit_price char(3),
MethodID numeric(1),
primary key(Payid),
foreign key (Cusid)references Customer(Cusid),
foreign key(invoiceno)references invoicce(invoiceno),
foreign key(methodid)references Method(methodid),
foreign key(SHIPID)references shipping(SHIPID),
foreign key (shopid)references shop(shopid),
foreign key(emid)references employee(emid)
);
